mapper.select:
SELECT QNA_NO, USER_NO, QNA_TITLE, QNA_ID, QNA_PW, QNA_CONTENT, QNA_CREATE_DATE, STATE, DEPTH, GROUP_NO
  FROM QNA
 WHERE QNA_NO = #{qnaNo}

mapper.update:
UPDATE QNA
SET QNA_TITLE = #{qnaTitle}
	,QNA_CONTENT = #{qnaContent}
WHERE QNA_NO = #{qnaNo}

mapper.delete:
DELETE QNA
  FROM QNA
 WHERE QNA_NO = #{qnaNo}
