mapper.select:
SELECT IS_HEART
  FROM HEART
 WHERE USER_NO = #{userNo}
   AND CLICK_USER_NO = #{clickUserNo}

SELECT COUNT(*)
  FROM HEART
 WHERE USER_NO = #{userNo}
   AND CLICK_USER_NO = #{clickUserNo}
   AND LOVE = 1

		SELECT COUNT(*)
		  FROM HEART
		 WHERE CLICK_USER_NO = #{clickUserNo}
		   AND USER_NO = #{userNo}
 		   AND HATE = -1

mapper.delete:
DELETE
  FROM HEART
 WHERE USER_NO = #{userNo}
   AND CLICK_USER_NO = #{clickUserNo}

mapper.select:
SELECT *
  FROM HEART
 WHERE USER_NO = #{userNo}
   AND CLICK_USER_NO = #{clickUserNo}
