mapper.delete:
DELETE
  FROM CART
 WHERE CART_NO = #{cartNo}
   AND USER_NO = #{userNo}

mapper.select:
SELECT TN_NO, PROD_NO, TN_PATH, TN_ORIGIN, TN_FILESYSTEM, IS_THUMBNAIL
  FROM PROD_THUMBNAIL
 WHERE PROD_NO = #{prodNo}

SELECT PROD_NO
  FROM CART
 WHERE USER_NO = #{userNo}
   AND PROD_NO = #{prodNo}
